import { useState } from "react";

const Homepage = () => {

    const [blogs, setBlogs] = useState([
        {
            title: "Blog 1",
            body: "This is blog 1",
            author: 'Jas',
            id: 1
        },
        {
            title: "Blog 2",
            body: "This is blog 2",
            author: 'Jos',
            id: 2
        },
        {
            title: "Blog 3",
            body: "This is blog 3",
            author: 'Jes',
            id: 3
        }
    ])

    return ( 
        <>
           <div className="home">
            <h2>Home Page</h2>
            {blogs.map((blog) => {
                return (
                    <div className="blog-preview" key={blog.id}>
                        <h2>{blog.title}</h2>
                        <p>{blog.body}</p>
                        <p>Author: {blog.author}</p>
                    </div>
                )
            })}
           </div>
        </> 
    );
}
 
export default Homepage;
